mapper.insert:

        insert into tbl_user(
        	name,
        	age,
        	phone,
        	address,
        	reg_date) 
		values (
			  #{name, jdbcType=VARCHAR}
			, #{age, jdbcType=NUMERIC }
			, #{phone, jdbcType=VARCHAR}
			, #{address, jdbcType=VARCHAR}
			, now()			
		)


