mapper.update:

update tbl_user set
	name = #{name, jdbcType=VARCHAR},
	age = #{age, jdbcType=NUMERIC },
	phone = #{phone, jdbcType=VARCHAR},
	address = #{address, jdbcType=VARCHAR}
where id = #{id}

mapper.select:
SELECT *
FROM tbl_user
WHERE id = #{id} AND password = #{password}
